import React, { FunctionComponent } from 'react';
import Pokemon from '../models/pokemon';
import './pokemon-card.css';

type Props = { //On def un nouveau type pr typescript
    pokemon: Pokemon
    borderColor?: string // syntax propre Ã  TS, variable is faculatif
  };

const PokemonCard: FunctionComponent<Props> = ({pokemon, borderColor = '#009688'}) => {
    const [color, setColor]= useState<string>(); //add a state to stock actual borderColor

    const showBorder = () => {
        setColor(borderColor);
    }

    const hideBorder = () => {
      setColor('#f5f5f5'); // we put border in grey color
  }

    return (
        <div className="col s6 m4" onMouseEnter={showBorder} onMouseLeave={hideBorder}>
          <div className="card horizontal" style={{ borderColor:borderColor}}>
            <div className="card-image"> 
              <img src={pokemon.picture} alt={pokemon.name}/>
            </div>
            <div className="card-stacked">
              <div className="card-content">
                <p>{pokemon.name}</p>
                <p><small>{pokemon.created.toString()}</small></p>
              </div>
            </div>
          </div> 
        </div>
      );
    }

export default PokemonCard;
